import { useEffect, useState } from "react";
import { fetchUsers, deleteUser } from "../api.js";
import { useNavigate } from "react-router-dom";

const Dashboard = () => {
  const [users, setUsers] = useState([]);
  const navigate = useNavigate();
  const token = localStorage.getItem("token");

  useEffect(() => {
    if (!token) return navigate("/");
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const { data } = await fetchUsers(token);
      setUsers(data);
    } catch (err) {
      alert("Failed to load users.");
    }
  };

  const handleDelete = async (id) => {
    await deleteUser(id, token);
    fetchData();
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <div className="max-w-5xl mx-auto bg-white shadow-lg rounded-lg p-6">
        <h2 className="text-2xl font-semibold text-center text-blue-600 mb-6">User Management</h2>
        <table className="w-full border-collapse">
          <thead>
            <tr className="bg-blue-500 text-white">
              <th className="p-3">Name</th>
              <th className="p-3">Email</th>
              <th className="p-3">Actions</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user) => (
              <tr key={user._id} className="border-b">
                <td className="p-3">{user.firstName} {user.lastName}</td>
                <td className="p-3">{user.email}</td>
                <td className="p-3">
                  <button onClick={() => handleDelete(user._id)}
                    className="bg-red-500 text-white px-3 py-1 rounded-lg hover:bg-red-600 transition">
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <button onClick={() => { localStorage.removeItem("token"); navigate("/"); }}
          className="mt-4 bg-gray-500 text-white py-2 px-4 rounded-lg hover:bg-gray-600">
          Logout
        </button>
      </div>
    </div>
  );
};

export default Dashboard;
